import React from "react";
import "./vistaTorneos.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Table, Button, Container } from "reactstrap";
import "react-datepicker/dist/react-datepicker.css";
//import iconoJuego1 from "./iconos/iconoJuego1.png";
//import iconoJuego2 from "./iconos/iconoJuego2.png";

class VistaTorneos extends React.Component {
  state = {
    data: [],
    dataFiltrada: [],
    busqueda: "",
  };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/torneo/readtorneos")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ data: data, dataFiltrada: data });
      })
      .catch((error) => {
        alert("Error al obtener datos del servidor:", error);
      });
  }

  handleChangeBuscar = async (e) => {
    e.persist();
    await this.setState({ busqueda: e.target.value });
    this.filtrarElementos();
  };

  filtrarElementos = () => {
    const dataFiltrada = this.state.data.filter(
      (elemento) =>
        elemento.nombre.toLowerCase().includes(this.state.busqueda.toLowerCase()) ||
        elemento.fechahora.toLowerCase().includes(this.state.busqueda.toLowerCase())
    );
    this.setState({ data: dataFiltrada });
  };



  render() {
    return (
      <>
      <h1 className="titulo-con-imagen" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/iconos/iconoJuego1.png)` }}>
        Â¡Torneos Registrados!
        </h1>


        <Container className="CRUDTorneo">
          <br />
          <div className="barraBusqueda">
            <input
              type="text"
              placeholder="Buscar"
              className="textField"
              name="busqueda"
              value={this.state.busqueda}
              onChange={this.handleChangeBuscar}
            />
            <Button className="btnBuscar" color="primary">
              Buscar
            </Button>
          </div>
          <br />
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Fecha y Hora</th>
              </tr>
            </thead>

            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.id}</td>
                  <td>{dato.nombre}</td>
                  <td>{dato.fechahora}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Container>
      </>
    );
  }

}

export default VistaTorneos;
