import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from 'reactstrap';
import './EditProfile.css';

function EditProfile() {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [correo, setCorreo] = useState('');
  const [contrasena, setContrasena] = useState('');
  const [foto, setFoto] = useState(null);

  const [modal, setModal] = useState(false);
  const navigate = useNavigate();

  const toggleModal = () => setModal(!modal);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFoto(file);
  };

  const handleGuardarCambios = async (e) => {
    e.preventDefault();

    // Aquí puedes enviar los datos al servidor para actualizar el perfil
    const datosActualizados = {
      nombre,
      apellido,
      correo,
      contrasena,
      foto,
      gamertag,
    };

    console.log('Datos actualizados:', datosActualizados);

    // Luego, puedes realizar una solicitud al servidor para manejar la actualización del perfil.
    // Utiliza fetch o axios según tu preferencia.
    try {
      const res = await fetch(`http://127.0.0.1:5000/register`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
          body: JSON.stringify(datosActualizados),
      });

      const data = await res.json();
      console.log('Respuesta del servidor:', data);

      // Puedes manejar la respuesta del servidor según tus necesidades
      // Por ejemplo, cerrar el modal o mostrar un mensaje de éxito
      toggleModal();
    } catch (error) {
      console.error('Error al enviar datos al servidor:', error);
      // Puedes manejar los errores y proporcionar retroalimentación al usuario si es necesario
    }
  };

  const handleClickVolver = () => {
    navigate(-1); // Navegar hacia atrás
  };

  return (
    <div className="EditProfile">
      <h1>Editar Perfil</h1>
      <form onSubmit={handleGuardarCambios}>
        <FormGroup>
          <Label for="nombre">Nombre:</Label>
          <Input
            type="text"
            id="nombre"
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="apellido">Apellido:</Label>
          <Input
            type="text"
            id="apellido"
            value={apellido}
            onChange={(e) => setApellido(e.target.value)}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="correo">Correo:</Label>
          <Input
            type="email"
            id="correo"
            value={correo}
            onChange={(e) => setCorreo(e.target.value)}
            required
          />
        </FormGroup>

        <FormGroup>
          <Label for="contrasena">Contraseña:</Label>
          <Input
            type="password"
            id="contrasena"
            value={contrasena}
            onChange={(e) => setContrasena(e.target.value)}
            required
          />
        </FormGroup>

        <FormGroup className="d-flex flex-column align-items-center">
            <Label for="foto">Foto de Perfil:</Label>      
            {/* Botón para abrir el explorador de archivos */}
            <Button color="primary" onClick={toggleModal}>
            Seleccionar Foto
            </Button>
        </FormGroup>

        <FormGroup>
          <Button color="primary" type="submit">
            Guardar Cambios
          </Button>
          <Button color="secondary" onClick={handleClickVolver}>
            Volver
          </Button>
        </FormGroup>
      </form>

      {/* Modal para la selección de foto */}
      <Modal isOpen={modal} toggle={toggleModal}>
        <ModalHeader toggle={toggleModal}>Seleccionar Foto</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="fotoModal">Foto</Label>
              <Input type="file" id="fotoModal" onChange={handleFileChange} />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggleModal}>
            Confirmar
          </Button>{' '}
          <Button color="secondary" onClick={toggleModal}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default EditProfile;
