import React from "react";
import "./CRUDAdmin.css";
import "bootstrap/dist/css/bootstrap.min.css";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalHeader,
  ModalBody,
  FormGroup,
  ModalFooter,
} from "reactstrap";

class CRUDAdmin extends React.Component {
  state = {
    data: [],
    dataFiltrada: [],
    modalActualizar: false,
    modalInsertar: false,
    formInsertar: {
      nombre: "",
      apellido: "",
      email: "",
      psswd: "",
    },
    formActualizar: {
      nombre: "",
      apellido: "",
      email: "",
    },
    busqueda: "",
  };

  componentDidMount() {
    fetch("http://127.0.0.1:5000/readadmin")
      .then((response) => response.json())
      .then((data) => {
        this.setState({ data: data, dataFiltrada: data });
      })
      .catch((error) => {
        alert("Error al obtener datos del servidor:", error);
      });
  }

  mostrarModalActualizar = (dato) => {
    this.setState({
      formActualizar: dato,
      modalActualizar: true,
    });
  };

  cerrarModalActualizar = () => {
    this.setState({ modalActualizar: false });
  };

  mostrarModalInsertar = () => {
    this.setState({
      modalInsertar: true,
    });
  };

  cerrarModalInsertar = () => {
    this.setState({ modalInsertar: false });
  };

  editar = () => {
    const { nombre, apellido, email } = this.state.formActualizar;
    fetch("http://127.0.0.1:5000/updateadmin", {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ nombre, apellido, email }),
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.setState({ modalActualizar: false });
      })
      .catch(error => {
        console.error("Error al editar administrador:", error);
        alert("Error al editar administrador. Por favor, inténtalo nuevamente más tarde.");
        this.setState({ modalActualizar: false });
      });
  };

  eliminar = (dato) => {
    var opcion = window.confirm("¿Estás segur@ de eliminar el elemento " + dato.id + "?");
    if (opcion === true) {
      fetch(`http://127.0.0.1:5000/deleteadmin`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: dato.idAdministrador }),
      })
        .then(response => response.json())
        .then(data => {
          // Actualizar el estado local solo si la eliminación en el servidor fue exitosa
          if (data.success) {
            var contador = 0;
            var arreglo = this.state.data;
            arreglo.map((registro) => {
              if (dato.id === registro.id) {
                arreglo.splice(contador, 1);
              }
              contador++;
            });
            this.setState({ data: arreglo, modalActualizar: false });
          } else {
            alert('Error al eliminar administrador en el servidor');
          }
        })
        .catch(error => {
          console.error("Error al eliminar administrador:", error);
          alert("Error al eliminar administrador. Por favor, inténtalo más tarde.");
        });
    }
  };


  // const res = await fetch(`http://127.0.0.1:5000/login`, {
  //       method: 'POST',
  //       headers: {
  //         'Content-type': 'application/json',
  //       },
  //       body: JSON.stringify({ email, password })
  //     });
  //     const data = await res.json();

  insertar = () => {
    const { nombre, apellido, email, psswd } = this.state.formInsertar;
    fetch("http://127.0.0.1:5000/insertadmin", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ nombre, apellido, email, psswd }),
    })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.setState({ modalInsertar: false });
      })
      .catch(error => {
        console.error("Error al insertar administrador:", error);
        alert("Error al insertar administrador. Por favor, inténtalo nuevamente más tarde.");
        this.setState({ modalInsertar: false });
      });
