import "./CRUDTorneo.css";
import "bootstrap/dist/css/bootstrap.min.css";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import React, { useState, useEffect } from 'react';


import {
  Table,
  Button,
  Container,
  Modal,
  ModalHeader,
  ModalBody,
  FormGroup,
  ModalFooter,
} from "reactstrap";

const CRUDTorneo = () => {
  const [data, setData] = useState([]);
  const [dataFiltrada, setDataFiltrada] = useState([]);
  const [modalActualizar, setModalActualizar] = useState(false);
  const [modalInsertar, setModalInsertar] = useState(false);
  const [modalEliminar, setModalEliminar] = useState(false);
  const [formInsertar, setFormInsertar] = useState({
    nombre: "",
    fechahora: new Date(),
  });
  const [formActualizar, setFormActualizar] = useState({
    id: null,
    nombre: "",
    fechahora: "",
  });
  const [torneoAEliminar, setTorneoAEliminar] = useState(null);
  const [busqueda, setBusqueda] = useState("");

  useEffect(() => {
    obtenerTorneos();
  }, []);

  const obtenerTorneos = () => {
    fetch("http://127.0.0.1:5000/torneo/readtorneos")
      .then((response) => response.json())
      .then((data) => {
        setData(data);
        setDataFiltrada(data);
      })
      .catch((error) => {
        alert("Error al obtener datos del servidor:", error);
      });
  };

  const mostrarTodosTorneos = () => {
    // Realiza la redirección a la nueva página
    window.location.href = "http://localhost:5000/torneo/readtorneos";
  };

  const mostrarModalActualizar = (dato) => {
    setFormActualizar({ ...dato });
    setModalActualizar(true);
  };

  const cerrarModalActualizar = () => {
    setModalActualizar(false);
  };

  const mostrarModalInsertar = () => {
    setModalInsertar(true);
  };

  const cerrarModalInsertar = () => {
    setModalInsertar(false);
  };

  const mostrarModalEliminar = (dato) => {
    setModalEliminar(true);
    setTorneoAEliminar(dato.id);
  };

  const cerrarModalEliminar = () => {
    setModalEliminar(false);
  };

  const editar = () => {
    fetch(`http://127.0.0.1:5000/torneo/updatetorneo/${formActualizar.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formActualizar),
    })
      .then((response) => response.json())
      .then(() => {
        obtenerTorneos();
        cerrarModalActualizar();
      })
      .catch((error) => {
        alert("Error al actualizar el torneo:", error);
      });
  };

  const eliminar = () => {
    fetch(`http://127.0.0.1:5000/torneo/deletetorneo/${torneoAEliminar}`, {
      method: "DELETE",
    })
      .then(() => {
        obtenerTorneos();
        cerrarModalEliminar();
      })
      .catch((error) => {
        alert("Error al eliminar el torneo:", error);
      });
  };

  const insertar = () => {
    const { nombre, fechahora } = formInsertar;
    fetch("http://127.0.0.1:5000/torneo/createtorneo", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ nombre, fechahora }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setModalInsertar(false);
        obtenerTorneos();
      })
      .catch((error) => {
        console.error("Error al insertar el torneo:", error);
        alert(
          "Error al insertar un torneo. Por favor, inténtalo más tarde."
        );
        setModalInsertar(false);
      });
  };

  const handleChangeInsertar = (e) => {
    setFormInsertar({
      ...formInsertar,
      [e.target.name]: e.target.value,
    });
  };

  const handleChangeFechaInsertar = (date) => {
    setFormInsertar((prevState) => ({
      ...prevState,
      fechahora: date,
    }));
  };

  const handleChangeActualizar = (e) => {
    const { name, value } = e.target;
    setFormActualizar((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleChangeBuscar = (e) => {
    const { value } = e.target;
    setBusqueda(value);
    filtrarElementos();
  };

  const filtrarElementos = () => {
    const dataFiltrada = data.filter(
      (elemento) =>
        elemento.nombre.toLowerCase().includes(busqueda.toLowerCase()) ||
        elemento.fechahora.toLowerCase().includes(busqueda.toLowerCase())
    );
    setDataFiltrada(dataFiltrada);
  };

  return (
    <Container className="CRUDTorneo">
      <br />
      <div className="d-flex justify-content-between">
        <Button
          style={{ marginRight: "10px" }}
          color="success"
          onClick={() => mostrarModalInsertar()}
        >
          Nuevo Torneo
        </Button>
        <Button
          style={{ marginRight: "10px" }}
          color="success"
          onClick={() => mostrarTodosTorneos()}
        >
          Ver Torneos Actuales
        </Button>
      </div>
      <div className="barraBusqueda">
        <input
          type="text"
          placeholder="Buscar"
          className="textField"
          name="busqueda"
          value={busqueda}
          onChange={handleChangeBuscar}
        />
        <Button className="btnBuscar" color="primary">
          Buscar
        </Button>
      </div>
      <br />
      <br />
      <Table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Fecha y Hora</th>
            <th>Acción</th>
          </tr>
        </thead>

        <tbody>
          {dataFiltrada.map((dato) => (
            <tr key={dato.id}>
              <td>{dato.id}</td>
              <td>{dato.nombre}</td>
              <td>{dato.fechahora}</td>

              <td>
                <Button
                  color="primary"
                  onClick={() => mostrarModalActualizar(dato)}
                >
                  Editar
                </Button>{" "}
                <Button
                  color="danger"
                  onClick={() => mostrarModalEliminar(dato)}
                >
                  Eliminar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* ... Modales ... */}
      <Modal isOpen={modalActualizar}>
        <ModalHeader>
          <div>
            <h3>Actualizar Torneo</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Nombre:</label>
            <input
              className="form-control"
              name="nombre"
              type="text"
              onChange={handleChangeActualizar}
              value={formActualizar.nombre}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha y Hora:</label>
            <DatePicker
              className="form-control"
              selected={formActualizar.fechahora}
              onChange={(date) => handleChangeFechaInsertar(date)}
              showTimeSelect
              timeFormat="HH:mm"
              timeIntervals={15}
              dateFormat="yyyy-MM-dd HH:mm:ss"
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={() => editar()}>
            Actualizar
          </Button>
          <Button color="danger" onClick={() => cerrarModalActualizar()}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>

      <Modal isOpen={modalInsertar}>
        <ModalHeader>
          <div>
            <h3>Insertar Torneo</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <label>Nombre:</label>
            <input
              className="form-control"
              name="nombre"
              type="text"
              onChange={handleChangeInsertar}
            />
          </FormGroup>
          <FormGroup>
            <label>Fecha y Hora:</label>
            <DatePicker
              className="form-control"
              selected={formInsertar.fechahora}
              onChange={(date) => handleChangeFechaInsertar(date)}
              showTimeSelect
              timeFormat="HH:mm"
              timeIntervals={15}
              dateFormat="yyyy-MM-dd HH:mm:ss"
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={() => insertar()}>
            Insertar
          </Button>
          <Button color="danger" onClick={() => cerrarModalInsertar()}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>

      <Modal isOpen={modalEliminar}>
        <ModalHeader>
          <div>
            <h3>Eliminar Torneo</h3>
          </div>
        </ModalHeader>
        <ModalBody>
          <p>¿Estás seguro que deseas eliminar el torneo?</p>
        </ModalBody>
        <ModalFooter>
          <Button color="danger" onClick={() => eliminar()}>
            Confirmar
          </Button>
          <Button color="secondary" onClick={() => cerrarModalEliminar()}>
            Cancelar
          </Button>
        </ModalFooter>
      </Modal>
    </Container>
  );
};

export default CRUDTorneo;
